<style scoped lang="scss">
.calculator-benefits {
  padding: 80px 0;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}

.title {
  font-size: 48px;
  font-weight: 700;
  text-align: center;
  color: #1a1a1a;
  margin-bottom: 60px;
  line-height: 1.2;

  &__accent {
    color: #1775F6;
  }

  @media (max-width: 768px) {
    font-size: 32px;
    margin-bottom: 40px;
  }
}

.benefits-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 30px;

  @media (min-width: 768px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (min-width: 1024px) {
    grid-template-columns: repeat(4, 1fr);
  }
}

.benefit-card {
  background: white;
  border-radius: 25px;
  padding: 30px 25px;
  text-align: center;
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(23, 117, 246, 0.15);
  }

  &__icon {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 85px;
    height: 85px;
    margin: 0 auto 25px;
    background: rgba(23, 117, 246, 0.1);
    border-radius: 50%;

    svg {
      width: 50px;
      height: 50px;
    }
  }

  &__title {
    font-size: 20px;
    font-weight: 600;
    color: #1a1a1a;
    margin-bottom: 15px;
    line-height: 1.3;
    min-height: 52px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  &__description {
    font-size: 16px;
    color: #6b7280;
    line-height: 1.5;
    margin: 0;
  }

  @media (max-width: 480px) {
    padding: 25px 20px;

    &__title {
      font-size: 18px;
      min-height: auto;
    }

    &__description {
      font-size: 14px;
    }
  }
}
</style>